	public void clickWarranty(WebDriver driver)
	{
		WebDriverWait wait = new WebDriverWait(driver, 100);
		System.out.println("After webdriver wait");
		driver.manage().timeouts().implicitlyWait(100, TimeUnit.SECONDS);
		driver.manage().timeouts().pageLoadTimeout(100, TimeUnit.SECONDS);
		try
		{
			System.out.println("clicking warranty link");
			wait.until(ExpectedConditions.visibilityOfElementLocated(obj_EsupportIncidentsPage.WarrantyLink()));
			CommonUtility.ClickOn(driver,obj_EsupportIncidentsPage.WarrantyLink());
			Set<String> allWH=driver.getWindowHandles(); 
			String currentwindow=driver.getWindowHandle();
			int count=allWH.size(); 
			System.out.println("Switched Window Handles and  window count is :: "+count);
			for(String wh:allWH)
			{

				Thread.sleep(10000);
				driver.switchTo().window(wh); 

				handleCookies(driver);
				Thread.sleep(2000);
			}
			driver.close();
			driver.switchTo().window(currentwindow);
			Report.put("Click on Warranty hyperlink(Expired, ProSupport, Basic, Prosupport Plus, etc)",
					"User must be redirected to product support Warranty page in a new tab" ,
					"User redirects to product support Warranty page in a new tab" , "PASS");
		}
		catch(Exception e)
		{
			e.printStackTrace();
			Report.put("Click on Warranty hyperlink(Expired, ProSupport, Basic, Prosupport Plus, etc)",
					"User must be redirected to product support Warranty page in a new tab" ,
					"User do not redirect to product support Warranty page in a new tab" , "FAIL");
			Assert.fail();
		}

	}

